    private void jButton_calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_calcularActionPerformed
        // TODO add your handling code here: ESCRIBIR CODIGO AQUÍ
        try {
            String nroCadena = jTextField_numero.getText();
            int nro = Integer.parseInt(nroCadena);
            double resul = Math.sqrt(nro);
            jLabel_resultado.setText("Resultado " + resul);
        } catch (NumberFormatException nf) {
            JOptionPane.showMessageDialog(this, "Error... Usted debe ingresar un número");
            jTextField_numero.requestFocus();
        }
    }//GEN-LAST:event_jButton_calcularActionPerformed

